<style media="screen">
.loader_wrapper{position:fixed;width:100%;height:100%;left:0;top:0;background:#565c64;background:-moz-linear-gradient(45deg,rgba(86,92,100,1) 0,rgba(243,243,243,1) 100%);background:-webkit-linear-gradient(45deg,rgba(86,92,100,1) 0,rgba(243,243,243,1) 100%);background:linear-gradient(45deg,rgba(86,92,100,1) 0,rgba(243,243,243,1) 100%);background-size:300%;background-position:100%;z-index:9999;transition:1s ease-in-out;-webkit-transition:1s ease-in-out;opacity:1;visibility:visible;animation:2.2s gradient forwards;-webkit-animation:2.2s gradient forwards}@keyframes gradient{from{background-position:100%}to{background-position:0}}.sobor{fill:#fff;transition:1s;-webkit-transition:1s}.rect{fill:none;stroke-width:3px;stroke:#fff;stroke-dasharray:706;transition:1s;-webkit-transition:1s}.svg-letters{fill:#fff;stroke-width:2px;stroke:#fff;fill-opacity:0;transition:1.6s;-webkit-transition:1.6s;stroke-dasharray:300;stroke-dashoffset:300;animation:2.8s lettered forwards 1.6s;-webkit-animation:2.8s lettered forwards 1.6s}@keyframes blured{from{transform:scale(2);filter:blur(12px);-webkit-transform:scale(2);-webkit-filter:blur(12px)}to{transform:scale(1.05);filter:blur(0);-webkit-transform:scale(1.05);-webkit-filter:blur(0)}}@keyframes lettered{from{fill-opacity:0;stroke-dashoffset:300}to{fill-opacity:1;stroke-dashoffset:0}}.loader_inner{width:230px;height:230px;position:absolute;top:50%;left:50%;transform:translate(-50%,-50%);-webkit-transform:translate(-50%,-50%);transition:.46s;-webkit-transition:.46s}.loader_wrapper svg{width:100%;height:100%;transform:scale(2);filter:blur(12px);animation:2s blured ease-in-out forwards;-webkit-transform:scale(2);-webkit-filter:blur(12px);-webkit-animation:2s blured ease-in-out forwards}.done{opacity:0;visibility:hidden}
</style>
<script>
  document.body.onload = function(){
    setTimeout(function(){
      var preloader = document.getElementById('preloader');
      if ( !preloader.classList.contains('done') ){
        preloader.classList.add('done')
      }
    }, 2800);
  }
</script>
